		private void setNumber(boolean tf) {
			if (params.number != tf)
				mustReDrawTopMenu = true;
			params.number = tf;
			if (!params.number)
				autonumber = false;
			else {
				if (params.mark)
					mustRedrawEverything(); // if star option is selected later
				params.mark = false;
			}
		}

